```typescript
// packages/engine-sync/src/sync-engine.ts

import type { Note, NoteId, SyncStatus, SyncResult, Conflict, ConflictResolution } from '@scribe/shared';
import { SyncDatabase } from './sync-database';
import { SyncCoordinator } from './sync-coordinator';
import { SyncTransport } from './sync-transport';
import { NetworkMonitor } from './network-monitor';
import { ConflictResolver } from './conflict-resolver';
import { computeContentHash } from './content-hash';
import { loadSyncConfig, type SyncConfig } from './sync-config';

export interface SyncEngineConfig {
  vaultPath: string;
  serverUrl?: string;
  apiKey?: string;
  pollIntervalMs?: number;
}

export class SyncEngine {
  private db: SyncDatabase;
  private coordinator: SyncCoordinator;
  private transport: SyncTransport | null = null;
  private networkMonitor: NetworkMonitor;
  private conflictResolver: ConflictResolver;
  private config: SyncConfig | null = null;
  private statusListeners: Set<(status: SyncStatus) => void> = new Set();
  private pollTimer: NodeJS.Timeout | null = null;

  private readonly vaultPath: string;
  private readonly pollIntervalMs: number;

  constructor(config: SyncEngineConfig) {
    this.vaultPath = config.vaultPath;
    this.pollIntervalMs = config.pollIntervalMs ?? 30000;

    // Initialize database
    const dbPath = `${config.vaultPath}/derived/sync.sqlite3`;
    this.db = new SyncDatabase(dbPath);

    // Initialize components
    this.networkMonitor = new NetworkMonitor();
    this.conflictResolver = new ConflictResolver(this.db);
  }

  async initialize(): Promise<void> {
    // Load config
    this.config = await loadSyncConfig(this.vaultPath);

    if (!this.config?.enabled) {
      return; // Sync disabled - don't initialize transport
    }

    // Initialize transport
    this.transport = new SyncTransport(
      this.config.serverUrl,
      this.config.apiKeyHash
    );

    // Initialize coordinator
    this.coordinator = new SyncCoordinator({
      db: this.db,
      transport: this.transport,
      conflictResolver: this.conflictResolver,
      onStatusChange: (status) => this.notifyStatusChange(status),
    });

    // Start polling if online
    if (this.networkMonitor.isOnline()) {
      this.startPolling();
    }

    // React to network changes
    this.networkMonitor.onStatusChange((online) => {
      if (online) {
        this.triggerSync();
        this.startPolling();
      } else {
        this.stopPolling();
        this.notifyStatusChange({ ...this.getStatus(), state: 'offline' });
      }
    });
  }

  async shutdown(): Promise<void> {
    this.stopPolling();
    this.db.close();
  }

  // Add sync metadata to a note
  addSyncMetadata(note: Note): Note {
    const version = (note.sync?.version ?? 0) + 1;
    const contentHash = computeContentHash(note);

    return {
      ...note,
      sync: {
        ...note.sync,
        version,
        contentHash,
        deviceId: await this.db.getDeviceId(),
      },
    };
  }

  // Queue a change for sync
  async queueChange(
    note: Note | { id: NoteId },
    operation: 'create' | 'update' | 'delete'
  ): Promise<void> {
    if (!this.config?.enabled) return;

    const noteId = 'id' in note ? note.id : (note as Note).id;
    const fullNote = operation === 'delete' ? undefined : note as Note;
    const version = fullNote?.sync?.version ?? 1;

    this.db.queueChange(noteId, operation, version, fullNote);

    // Update status
    this.notifyStatusChange(this.getStatus());

    // Trigger immediate sync attempt if online
    if (this.networkMonitor.isOnline()) {
      // Debounce to batch rapid changes
      setTimeout(() => this.triggerPush(), 1000);
    }
  }

  // Manual sync triggers
  async triggerSync(): Promise<SyncResult> {
    if (!this.transport) {
      return { pushed: 0, pulled: 0, conflicts: 0, errors: ['Sync disabled'] };
    }
    return this.coordinator.sync();
  }

  async triggerPush(): Promise<SyncResult> {
    if (!this.transport) {
      return { pushed: 0, pulled: 0, conflicts: 0, errors: ['Sync disabled'] };
    }
    return this.coordinator.push();
  }

  async triggerPull(): Promise<SyncResult> {
    if (!this.transport) {
      return { pushed: 0, pulled: 0, conflicts: 0, errors: ['Sync disabled'] };
    }
    return this.coordinator.pull();
  }

  // Conflict management
  async getConflicts(): Promise<Conflict[]> {
    return this.db.getConflicts();
  }

  async resolveConflict(noteId: NoteId, resolution: ConflictResolution): Promise<void> {
    await this.conflictResolver.resolve(noteId, resolution);
    this.notifyStatusChange(this.getStatus());
  }

  // Status
  getStatus(): SyncStatus {
    if (!this.config?.enabled) {
      return { state: 'disabled', pendingChanges: 0, conflictCount: 0 };
    }

    if (!this.networkMonitor.isOnline()) {
      return {
        state: 'offline',
        pendingChanges: this.db.getPendingCount(),
        conflictCount: this.db.getConflictCount(),
      };
    }

    // TODO: Track syncing state in coordinator
    return {
      state: 'idle',
      pendingChanges: this.db.getPendingCount(),
      conflictCount: this.db.getConflictCount(),
      lastSyncAt: this.config.lastSyncSequence, // TODO: Store actual timestamp
    };
  }

  onStatusChange(callback: (status: SyncStatus) => void): () => void {
    this.statusListeners.add(callback);
    return () => this.statusListeners.delete(callback);
  }

  async getDeviceId(): Promise<string> {
    return this.db.getDeviceId();
  }

  // Private methods
  private startPolling(): void {
    if (this.pollTimer) return;
    this.pollTimer = setInterval(() => this.triggerSync(), this.pollIntervalMs);
  }

  private stopPolling(): void {
    if (this.pollTimer) {
      clearInterval(this.pollTimer);
      this.pollTimer = null;
    }
  }

  private notifyStatusChange(status: SyncStatus): void {
    for (const listener of this.statusListeners) {
      listener(status);
    }
  }
}

// Factory function
export async function createSyncEngine(config: SyncEngineConfig): Promise<SyncEngine> {
  const engine = new SyncEngine(config);
  await engine.initialize();
  return engine;
}
