  /** Get current sync status */
  getStatus(): Promise<SyncStatus>;

  /** Manually trigger a sync cycle */
  trigger(): Promise<SyncResult>;

  /** Get list of unresolved conflicts */
  getConflicts(): Promise<Conflict[]>;

