// Pattern 2: Handler with optional sync (e.g., notes:save with sync trigger)
ipcMain.handle(IPC_CHANNELS.NOTES_SAVE, withEngines(deps, async (engines, note: Note) => {
  await engines.vault.save(note);
  // ... other engine updates ...

  // Optional sync trigger
