import { createSyncEngine, type SyncEngine } from '@scribe/engine-sync';
import { loadSyncConfig, type SyncConfig } from './config/sync-config';

// Add to deps
const deps: HandlerDependencies = {
  vault: null,
  graphEngine: null,
  searchEngine: null,
  taskIndex: null,
  mainWindow: null,
  syncEngine: null, // NEW
};

/**
 * Initialize sync engine if enabled in config.
 * Call after vault is loaded.
 */
async function initializeSyncEngine(vaultPath: string): Promise<void> {
  // Load sync configuration
  const syncConfig = await loadSyncConfig(vaultPath);

  if (!syncConfig.enabled) {
    console.log('Sync is disabled, skipping SyncEngine initialization');
    return;
  }

  // Create sync database path
  const syncDbPath = path.join(vaultPath, 'derived', 'sync.sqlite3');

  // Create sync engine
  deps.syncEngine = createSyncEngine({
    vaultPath,
    databasePath: syncDbPath,
    serverUrl: syncConfig.serverUrl,
    credentials: syncConfig.credentials, // May be null if not logged in
    config: {
      syncIntervalMs: syncConfig.syncIntervalMs ?? 30000,
      autoSync: syncConfig.autoSync ?? true,
      conflictStrategy: syncConfig.conflictStrategy ?? 'manual',
    },
    // Wire up to EngineOrchestrator for saving pulled notes
    onSaveNote: async (note) => {
      if (deps.vault) {
        // Save directly to vault (skip sync tracking to avoid loop)
        await deps.vault.save(note);
        deps.graphEngine?.addNote(note);
        deps.searchEngine?.indexNote(note);
        deps.taskIndex?.indexNote(note);
      }
    },
    onDeleteNote: async (noteId) => {
      if (deps.vault) {
        await deps.vault.delete(noteId);
        deps.graphEngine?.removeNote(noteId);
        deps.searchEngine?.removeNote(noteId);
        deps.taskIndex?.removeNote(noteId);
      }
    },
    onStatusChange: (status) => {
      // Forward to renderer
      deps.mainWindow?.webContents.send(IPC_CHANNELS.SYNC_STATUS_CHANGED, status);
    },
  });

  // Initialize sync engine (loads local state, starts auto-sync if enabled)
  await deps.syncEngine.initialize();

  console.log('SyncEngine initialized');
}

/**
 * Hook into EngineOrchestrator to track changes for sync.
 * Call after both orchestrator and sync engine are initialized.
 */
function wireOrchestratorToSync(orchestrator: EngineOrchestrator): void {
  if (!deps.syncEngine) {
    return;
  }

  // Override orchestrator save to notify sync
  const originalSave = orchestrator.saveNote.bind(orchestrator);
  orchestrator.saveNote = async (note) => {
    const result = await originalSave(note);

    // Notify sync engine of change (it will decide whether to track)
    if (deps.syncEngine?.isEnabled()) {
      await deps.syncEngine.trackChange(note, 'update');
    }

    return result;
  };

  // Override orchestrator delete to notify sync
  const originalDelete = orchestrator.deleteNote.bind(orchestrator);
  orchestrator.deleteNote = async (noteId) => {
    const result = await originalDelete(noteId);

    if (deps.syncEngine?.isEnabled()) {
      deps.syncEngine.trackDelete(noteId);
    }

    return result;
  };
}

// In app ready handler, after vault initialization:
app.whenReady().then(async () => {
  // ... existing vault/engine initialization ...

  // Initialize sync engine (after vault)
  if (vaultPath) {
    await initializeSyncEngine(vaultPath);

    // Wire orchestrator to sync
    if (orchestrator) {
      wireOrchestratorToSync(orchestrator);
    }
  }

  // ... rest of initialization ...
});

// In app quit handler:
app.on('before-quit', async () => {
  // Shutdown sync engine gracefully
  if (deps.syncEngine) {
    await deps.syncEngine.shutdown();
