import { readFile, writeFile } from 'fs/promises';
import { join } from 'path';

export interface SyncConfig {
  enabled: boolean;
  serverUrl: string;
  syncIntervalMs: number;
  autoSync: boolean;
  conflictStrategy: 'auto' | 'manual';
  credentials?: {
    email: string;
    refreshToken: string;
  };
}

const DEFAULT_CONFIG: SyncConfig = {
  enabled: false, // Opt-in: disabled by default
  serverUrl: 'https://sync.scribe.app',
  syncIntervalMs: 30000,
  autoSync: true,
  conflictStrategy: 'manual',
};

export async function loadSyncConfig(vaultPath: string): Promise<SyncConfig> {
  const configPath = join(vaultPath, '.scribe', 'sync.json');

  try {
    const content = await readFile(configPath, 'utf-8');
    return { ...DEFAULT_CONFIG, ...JSON.parse(content) };
  } catch {
    // No config file = sync disabled
    return DEFAULT_CONFIG;
  }
}

export async function saveSyncConfig(
  vaultPath: string,
  config: Partial<SyncConfig>
): Promise<void> {
  const configPath = join(vaultPath, '.scribe', 'sync.json');
  const current = await loadSyncConfig(vaultPath);
  const updated = { ...current, ...config };

  await writeFile(configPath, JSON.stringify(updated, null, 2));
}
