```typescript
import { ipcRenderer } from 'electron';
import { IPC_CHANNELS, type SyncAPI, type SyncStatus, type SyncConflict } from '@scribe/shared';

/**
 * Sync API exposed to renderer via preload bridge.
 * 
 * This matches the SyncAPI interface in @scribe/shared/ipc-contract.
 */
export const syncApi: SyncAPI = {
  /**
   * Check if sync feature is enabled.
   */
  isEnabled: () => ipcRenderer.invoke(IPC_CHANNELS.SYNC_IS_ENABLED),

  /**
   * Get current sync status.
   */
  getStatus: () => ipcRenderer.invoke(IPC_CHANNELS.SYNC_GET_STATUS),

  /**
   * Trigger immediate sync cycle.
   */
  syncNow: () => ipcRenderer.invoke(IPC_CHANNELS.SYNC_NOW),

  /**
   * Get pending conflicts requiring resolution.
   */
  getConflicts: () => ipcRenderer.invoke(IPC_CHANNELS.SYNC_GET_CONFLICTS),

  /**
   * Resolve a specific conflict.
   */
  resolveConflict: (noteId: string, resolution: 'local' | 'remote' | 'keepBoth') =>
    ipcRenderer.invoke(IPC_CHANNELS.SYNC_RESOLVE_CONFLICT, noteId, resolution),

  /**
   * Enable sync with credentials.
   */
  enable: (credentials: { email: string; token: string }) =>
    ipcRenderer.invoke(IPC_CHANNELS.SYNC_ENABLE, credentials),

  /**
   * Disable sync.
   */
  disable: () => ipcRenderer.invoke(IPC_CHANNELS.SYNC_DISABLE),

  /**
   * Get sync configuration.
   */
  getConfig: () => ipcRenderer.invoke(IPC_CHANNELS.SYNC_GET_CONFIG),

  /**
   * Update sync configuration.
   */
  setConfig: (config: Partial<{ syncIntervalMs: number; autoSync: boolean }>) =>
    ipcRenderer.invoke(IPC_CHANNELS.SYNC_SET_CONFIG, config),

  /**
   * Subscribe to sync status changes.
   * Returns unsubscribe function.
   */
  onStatusChange: (callback: (status: SyncStatus) => void) => {
    const handler = (_event: Electron.IpcRendererEvent, status: SyncStatus) => {
      callback(status);
    };
    ipcRenderer.on(IPC_CHANNELS.SYNC_STATUS_CHANGED, handler);
    return () => {
      ipcRenderer.removeListener(IPC_CHANNELS.SYNC_STATUS_CHANGED, handler);
    };
  },

  /**
   * Subscribe to conflict detection events.
   * Returns unsubscribe function.
   */
  onConflict: (callback: (conflict: SyncConflict) => void) => {
    const handler = (_event: Electron.IpcRendererEvent, conflict: SyncConflict) => {
      callback(conflict);
    };
    ipcRenderer.on(IPC_CHANNELS.SYNC_CONFLICT_DETECTED, handler);
    return () => {
      ipcRenderer.removeListener(IPC_CHANNELS.SYNC_CONFLICT_DETECTED, handler);
    };
  },
};
