// apps/sync-server/src/db/queries.ts

import type { D1Database, D1Result } from '@cloudflare/workers-types';

export interface User {
  id: string;
  email: string;
  api_key_hash: string;
  created_at: number;
  storage_used_bytes: number;
  note_count: number;
}

export interface Note {
  id: string;
  user_id: string;
  version: number;
  content_hash: string;
  content: string;
  note_type: string | null;
  created_at: number;
  updated_at: number;
  deleted_at: number | null;
}

export interface ChangeLogEntry {
  sequence: number;
  user_id: string;
  note_id: string;
  device_id: string | null;
  operation: 'create' | 'update' | 'delete';
  version: number;
  content_hash: string | null;
  created_at: number;
}

export class SyncQueries {
  constructor(private db: D1Database) {}

  // User queries
  async getUserByEmail(email: string): Promise<User | null> {
    return this.db.prepare('SELECT * FROM users WHERE email = ?').bind(email).first<User>();
  }

  async getUserByApiKeyHash(hash: string): Promise<User | null> {
    return this.db.prepare('SELECT * FROM users WHERE api_key_hash = ?').bind(hash).first<User>();
  }

  async createUser(id: string, email: string, apiKeyHash: string): Promise<void> {
    await this.db.prepare(
      'INSERT INTO users (id, email, api_key_hash) VALUES (?, ?, ?)'
    ).bind(id, email, apiKeyHash).run();
  }

  // Note queries
  async getNote(userId: string, noteId: string): Promise<Note | null> {
    return this.db.prepare(
      'SELECT * FROM notes WHERE user_id = ? AND id = ? AND deleted_at IS NULL'
    ).bind(userId, noteId).first<Note>();
  }

  async upsertNote(
    userId: string,
    noteId: string,
    version: number,
    contentHash: string,
    content: string,
    noteType: string | null
  ): Promise<void> {
    await this.db.prepare(`
      INSERT INTO notes (id, user_id, version, content_hash, content, note_type, updated_at)
      VALUES (?, ?, ?, ?, ?, ?, unixepoch() * 1000)
      ON CONFLICT(id, user_id) DO UPDATE SET
        version = excluded.version,
        content_hash = excluded.content_hash,
        content = excluded.content,
        note_type = excluded.note_type,
        updated_at = unixepoch() * 1000
    `).bind(noteId, userId, version, contentHash, content, noteType).run();
  }

  async softDeleteNote(userId: string, noteId: string): Promise<void> {
    await this.db.prepare(
      'UPDATE notes SET deleted_at = unixepoch() * 1000 WHERE user_id = ? AND id = ?'
    ).bind(userId, noteId).run();
  }

  // Change log queries
  async appendChangeLog(
    userId: string,
    noteId: string,
    deviceId: string | null,
    operation: string,
    version: number,
    contentHash: string | null
  ): Promise<number> {
    const result = await this.db.prepare(`
      INSERT INTO change_log (user_id, note_id, device_id, operation, version, content_hash)
      VALUES (?, ?, ?, ?, ?, ?)
    `).bind(userId, noteId, deviceId, operation, version, contentHash).run();
    return result.meta.last_row_id;
  }

  async getChangesSince(
    userId: string,
    sinceSequence: number,
    limit: number
  ): Promise<ChangeLogEntry[]> {
    return this.db.prepare(`
      SELECT * FROM change_log
      WHERE user_id = ? AND sequence > ?
      ORDER BY sequence ASC
      LIMIT ?
    `).bind(userId, sinceSequence, limit).all<ChangeLogEntry>().then(r => r.results);
  }
}
```

