# [Phase 3.8] Implement auth endpoints (register, login, refresh)

## Problem Statement
Implement authentication endpoints for user registration, login, and token refresh. Uses PBKDF2 password hashing for Workers compatibility.

## Implementation

### File: `apps/sync-server/src/routes/auth.ts`

```typescript
import { Hono } from 'hono';
import type { Env } from '../types';
import { hashPassword, verifyPassword, createToken } from '../middleware/auth';

const ACCESS_TOKEN_EXPIRY = 15 * 60; // 15 minutes
const REFRESH_TOKEN_EXPIRY = 30 * 24 * 60 * 60; // 30 days

interface RegisterRequest {
  email: string;
  password: string;
}

interface LoginRequest {
  email: string;
  password: string;
}

interface RefreshRequest {
  refreshToken: string;
}

interface AuthResponse {
  accessToken: string;
  refreshToken: string;
  expiresIn: number;
  user: {
    id: string;
    email: string;
  };
}

export const authRoutes = new Hono<{ Bindings: Env }>();

/**
 * POST /api/auth/register
 * 
 * Create a new account.
 */
authRoutes.post('/register', async (c) => {
  const body = await c.req.json<RegisterRequest>();

  // Validate input
  if (!body.email || !isValidEmail(body.email)) {
    return c.json({ error: 'Invalid email' }, 400);
  }

  if (!body.password || body.password.length < 8) {
    return c.json({ error: 'Password must be at least 8 characters' }, 400);
  }

  // Check if email already exists
  const existing = await c.env.DB.prepare(
    'SELECT id FROM users WHERE email = ?'
  ).bind(body.email.toLowerCase()).first();

  if (existing) {
    return c.json({ error: 'Email already registered' }, 409);
  }

  // Create user
  const userId = crypto.randomUUID();
  const passwordHash = await hashPassword(body.password);
  const now = Date.now();

  await c.env.DB.prepare(
    `INSERT INTO users (id, email, password_hash, created_at, updated_at)
     VALUES (?, ?, ?, ?, ?)`
  ).bind(userId, body.email.toLowerCase(), passwordHash, now, now).run();

  // Generate tokens
  const tokens = await generateTokens(userId, body.email, c.env.JWT_SECRET);

  const response: AuthResponse = {
    ...tokens,
    user: { id: userId, email: body.email.toLowerCase() },
  };

  return c.json(response, 201);
});

/**
 * POST /api/auth/login
 * 
 * Authenticate and get tokens.
 */
authRoutes.post('/login', async (c) => {
  const body = await c.req.json<LoginRequest>();

  if (!body.email || !body.password) {
    return c.json({ error: 'Email and password required' }, 400);
  }

  // Find user
  const user = await c.env.DB.prepare(
    'SELECT id, email, password_hash FROM users WHERE email = ?'
  ).bind(body.email.toLowerCase()).first<{ id: string; email: string; password_hash: string }>();

  if (!user) {
    return c.json({ error: 'Invalid credentials' }, 401);
  }

  // Verify password
  const valid = await verifyPassword(body.password, user.password_hash);
  if (!valid) {
    return c.json({ error: 'Invalid credentials' }, 401);
  }

  // Update last login
  await c.env.DB.prepare(
    'UPDATE users SET last_login_at = ? WHERE id = ?'
  ).bind(Date.now(), user.id).run();

  // Generate tokens
  const tokens = await generateTokens(user.id, user.email, c.env.JWT_SECRET);

  const response: AuthResponse = {
    ...tokens,
    user: { id: user.id, email: user.email },
  };

  return c.json(response);
});

/**
 * POST /api/auth/refresh
 * 
 * Exchange refresh token for new access token.
 */
authRoutes.post('/refresh', async (c) => {
  const body = await c.req.json<RefreshRequest>();

  if (!body.refreshToken) {
    return c.json({ error: 'Refresh token required' }, 400);
  }

  try {
    // Verify refresh token
    const payload = await verifyRefreshToken(body.refreshToken, c.env.JWT_SECRET);

    // Check if user still exists
    const user = await c.env.DB.prepare(
      'SELECT id, email FROM users WHERE id = ?'
    ).bind(payload.sub).first<{ id: string; email: string }>();

    if (!user) {
      return c.json({ error: 'User not found' }, 401);
    }

    // Generate new tokens
    const tokens = await generateTokens(user.id, user.email, c.env.JWT_SECRET);

    const response: AuthResponse = {
      ...tokens,
      user: { id: user.id, email: user.email },
    };

    return c.json(response);
  } catch (error) {
    return c.json({ error: 'Invalid or expired refresh token' }, 401);
  }
});

/**
 * POST /api/auth/logout
 * 
 * Invalidate refresh token (optional - for token blacklist).
 */
authRoutes.post('/logout', async (c) => {
  // For stateless JWT, client just discards tokens
  // Could add token blacklist in KV for immediate invalidation
  return c.json({ success: true });
});

async function generateTokens(
  userId: string,
  email: string,
  secret: string
): Promise<{ accessToken: string; refreshToken: string; expiresIn: number }> {
  const now = Math.floor(Date.now() / 1000);

  const accessToken = await createToken({
    sub: userId,
    email,
    type: 'access',
    exp: now + ACCESS_TOKEN_EXPIRY,
  }, secret);

  const refreshToken = await createToken({
    sub: userId,
    email,
    type: 'refresh',
    exp: now + REFRESH_TOKEN_EXPIRY,
  }, secret);

  return {
    accessToken,
    refreshToken,
    expiresIn: ACCESS_TOKEN_EXPIRY,
  };
}

function isValidEmail(email: string): boolean {
  return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
}
```

