  ↻ Syncing...       - Animated spinner, sync in progress  
  ⬆ 3 pending        - Yellow up arrow, changes queued
  ⚠ 2 conflicts      - Orange warning, clickable to open modal
  ✕ Offline          - Gray X, no network connection
  ⚠ Error            - Red warning, sync error (clickable for details)
  (hidden)           - When sync is disabled, show nothing
```

## Implementation

```tsx
// apps/desktop/renderer/src/components/sync/SyncStatusIndicator.tsx

import { useSyncStatus } from '../../hooks/useSyncStatus';
import * as styles from './SyncStatusIndicator.css';

export function SyncStatusIndicator() {
  const { status, openConflicts, openSettings } = useSyncStatus();

  // Don't show anything if sync is disabled
  if (status.state === 'disabled') {
    return null;
  }

  const handleClick = () => {
    if (status.conflictCount > 0) {
      openConflicts();
    } else if (status.error) {
      openSettings();
    }
  };

  return (
    <button
      className={styles.indicator}
      onClick={handleClick}
      disabled={status.state === 'syncing'}
      title={getTooltip(status)}
    >
      <span className={styles.icon[status.state]}>
        {getIcon(status)}
      </span>
      <span className={styles.label}>
        {getLabel(status)}
      </span>
    </button>
  );
}

function getIcon(status: SyncStatus): string {
  switch (status.state) {
    case 'idle':
      return status.pendingChanges > 0 ? '⬆' : '✓';
    case 'syncing':
      return '↻';
    case 'offline':
      return '✕';
    case 'error':
      return '⚠';
    default:
      return '';
  }
}

function getLabel(status: SyncStatus): string {
  if (status.conflictCount > 0) {
    return `${status.conflictCount} conflict${status.conflictCount > 1 ? 's' : ''}`;
  }

  switch (status.state) {
    case 'idle':
      return status.pendingChanges > 0 
        ? `${status.pendingChanges} pending` 
        : 'Synced';
    case 'syncing':
      return 'Syncing...';
    case 'offline':
      return 'Offline';
    case 'error':
      return 'Error';
    default:
      return '';
  }
}

function getTooltip(status: SyncStatus): string {
  if (status.lastSyncAt) {
    const ago = formatRelativeTime(status.lastSyncAt);
    return `Last sync: ${ago}`;
  }
  return getLabel(status);
}
