            type: 'wiki-link',
            noteTitle: 'New Note',
            displayText: 'New Note',
            targetId: null
          }]
        }]
      }
    };
    expect(extractLinks(content)).toEqual([]);
  });

  it('extracts mix of wiki-links and regular links', () => {
    const content = {
      root: {
        children: [{
          type: 'paragraph',
          children: [
            { type: 'wiki-link', noteTitle: 'A', displayText: 'A', targetId: 'id-1' },
            { type: 'link', url: 'note://id-2' }
          ]
        }]
      }
    };
    expect(extractLinks(content)).toContain('id-1');
