import { format, parseISO, isSameDay, startOfDay, isValid } from 'date-fns';
import type { Note, LexicalState } from '@scribe/shared';
import type { TemplateConfig, TemplateContext } from './types';
import { registerTemplate } from './registry';

/**
 * Create initial content for daily notes: a single empty bullet list.
 * No H1 heading - title is displayed in the header only.
 */
export function createDailyContent(): LexicalState {
  return {
    root: {
      children: [
        {
          type: 'list',
          listType: 'bullet',
          children: [
            {
              type: 'listitem',
              children: [],
              direction: null,
              format: '',
              indent: 0,
              version: 1,
            },
          ],
          direction: null,
          format: '',
          indent: 0,
          version: 1,
        },
      ],
      type: 'root',
      format: '',
      indent: 0,
      version: 1,
    },
    type: 'daily',
  };
}

/**
 * Get display title for a daily note.
 * - Returns "Today" for today's date
 * - Returns "MM/dd/yyyy" for other dates
 * 
 * @param note - The daily note (title is stored as ISO date YYYY-MM-DD)
 */
export function getDailyDisplayTitle(note: Note): string {
  const noteDate = parseISO(note.title);

  // Handle invalid dates gracefully
  if (!isValid(noteDate)) {
    return note.title;
  }

  const today = startOfDay(new Date());

  if (isSameDay(noteDate, today)) {
    return 'Today';
  }
  return format(noteDate, 'MM/dd/yyyy');
}

export const dailyTemplate: TemplateConfig = {
  type: 'daily',
  displayName: 'Daily Note',
  defaultTags: ['daily'],
  generateTitle: (context: TemplateContext) => format(context.date, 'yyyy-MM-dd'),
  renderTitle: (note: Note) => getDailyDisplayTitle(note),
  generateContent: () => createDailyContent(),
  contextPanelConfig: {
    sections: [
      { type: 'linked-mentions' },
      { type: 'tasks', placeholder: true },
      { type: 'references' },
      { type: 'calendar' },
    ],
  },
  dateSearchable: true,
};

// Auto-register on import
registerTemplate(dailyTemplate);
```

