### 3. Update section rendering logic

```typescript
function ContextPanel({ note, onSelectBacklink, onNavigate, onNoteUpdate, ... }: ContextPanelProps) {
  // Get template for current note type (if any)
  const template = note ? getTemplate(note.type) : undefined;
  const sections = template?.contextPanelConfig.sections ?? defaultContextPanelSections;

  // Existing backlinks logic...
  const backlinks = useMemo(() => {
    if (!note) return [];
    return window.scribe.graph.getBacklinks(note.id);
  }, [note?.id]);

  return (
    <aside className={clsx(styles.contextPanel, !isOpen && styles.closed)}>
      <div className={styles.panelInner}>
        <h2 className={styles.sectionLabel}>Context</h2>

        {sections.map((section, index) => {
          switch (section.type) {
            case 'linked-mentions':
              return (
                <LinkedMentions 
                  key={index} 
                  backlinks={backlinks} 
                  onSelectBacklink={onSelectBacklink} 
                />
              );

            case 'attendees':
              // Only render for meetings
              return note?.type === 'meeting' ? (
                <AttendeesWidget 
                  key={index} 
                  note={note} 
                  onNavigate={onNavigate}
                  onNoteUpdate={onNoteUpdate}
                />
              ) : null;

            case 'tasks':
              return (
                <TasksWidget 
                  key={index} 
                  placeholder={section.placeholder} 
                />
              );

            case 'references':
              return note ? (
                <ReferencesWidget 
                  key={index} 
                  note={note} 
                  onNavigate={onNavigate} 
                />
              ) : null;

            case 'calendar':
              return (
                <React.Fragment key={index}>
                  <h2 className={styles.sectionLabel}>Calendar</h2>
                  <CalendarWidget />
                </React.Fragment>
              );

            default:
              return null;
          }
        })}
      </div>
    </aside>
  );
}
```

