import { describe, it, expect, beforeEach, afterEach } from 'vitest';
import { setupTestVault, cleanupTestVault, launchApp, closeApp } from './test-helpers';
import { format, subDays, addDays } from 'date-fns';

describe('Daily Notes Integration', () => {
  let app: ElectronApp;
  let vaultPath: string;

  beforeEach(async () => {
    vaultPath = await setupTestVault();
    app = await launchApp({ vaultPath });
  });

  afterEach(async () => {
    await closeApp(app);
    await cleanupTestVault(vaultPath);
  });

  describe('Today command', () => {
    it('creates daily note when none exists', async () => {
      // Open command palette
      await app.keyboard.press('Control+k');

      // Type "Today" and select command
      await app.keyboard.type('Today');
      await app.keyboard.press('Enter');

      // Wait for navigation
      await app.waitForSelector('[data-testid="editor-root"]');

      // Verify note was created with correct properties
      const note = await app.evaluate(() => {
        const noteId = window.currentNoteId;
        return window.scribe.notes.read(noteId);
      });

      const today = format(new Date(), 'yyyy-MM-dd');
      expect(note.type).toBe('daily');
      expect(note.title).toBe(today);
      expect(note.tags).toContain('daily');
      expect(note.daily?.date).toBe(today);
    });

    it('opens existing daily note (idempotent)', async () => {
      // Create daily note first
      const firstNote = await app.evaluate(() => 
        window.scribe.daily.getOrCreate()
      );

      // Execute Today command again
      await app.keyboard.press('Control+k');
      await app.keyboard.type('Today');
      await app.keyboard.press('Enter');

      // Verify same note opened
      const currentNoteId = await app.evaluate(() => window.currentNoteId);
      expect(currentNoteId).toBe(firstNote.id);

      // Verify only one daily note exists
      const dailyNotes = await app.evaluate(() => 
        window.scribe.notes.list().filter(n => n.type === 'daily')
      );
      expect(dailyNotes).toHaveLength(1);
    });

    it('displays "Today" in header for today\'s note', async () => {
      await app.evaluate(() => window.scribe.daily.getOrCreate());
      await app.keyboard.press('Control+k');
      await app.keyboard.type('Today');
      await app.keyboard.press('Enter');

      // Verify header shows "Today"
      const headerText = await app.textContent('[data-testid="note-header-title"]');
      expect(headerText).toBe('Today');
    });
  });

  describe('Daily note title rendering', () => {
    it('displays formatted date for past daily notes', async () => {
      // Create daily note for yesterday (manual creation)
      const yesterday = subDays(new Date(), 1);
      const isoDate = format(yesterday, 'yyyy-MM-dd');

      const note = await app.evaluate((date) => {
        return window.scribe.notes.create({
          type: 'daily',
          title: date,
          tags: ['daily'],
          daily: { date },
        });
      }, isoDate);

      // Navigate to it
      await app.evaluate((id) => window.navigateToNote(id), note.id);

      // Verify header shows MM/dd/yyyy format
      const headerText = await app.textContent('[data-testid="note-header-title"]');
      const expectedFormat = format(yesterday, 'MM/dd/yyyy');
      expect(headerText).toBe(expectedFormat);
    });
  });

  describe('Search by date', () => {
    it('finds daily note by MM/dd/yyyy format', async () => {
      // Create daily note for today
      await app.evaluate(() => window.scribe.daily.getOrCreate());

      // Search by formatted date
      const today = format(new Date(), 'MM/dd/yyyy');
      await app.keyboard.press('Control+k');
      await app.keyboard.type(today);

      // Verify daily note appears in results
      await app.waitForSelector('[data-testid="search-result"]');
      const results = await app.$$('[data-testid="search-result"]');
      expect(results.length).toBeGreaterThan(0);
    });

    it('shows create option when no daily note exists', async () => {
      // Search for future date (no note exists)
      const futureDate = format(addDays(new Date(), 30), 'MM/dd/yyyy');
      await app.keyboard.press('Control+k');
      await app.keyboard.type(futureDate);

      // Verify "Create daily note for..." option appears
      await app.waitForSelector('[data-testid="create-daily-option"]');
      const optionText = await app.textContent('[data-testid="create-daily-option"]');
      expect(optionText).toContain('Create daily note for');
    });

    it('creates daily note from search create option', async () => {
      const futureDate = format(addDays(new Date(), 30), 'MM/dd/yyyy');
      const isoDate = format(addDays(new Date(), 30), 'yyyy-MM-dd');

      await app.keyboard.press('Control+k');
      await app.keyboard.type(futureDate);
      await app.waitForSelector('[data-testid="create-daily-option"]');
      await app.click('[data-testid="create-daily-option"]');

      // Verify note was created
      const note = await app.evaluate((date) => 
        window.scribe.daily.find(date),
        isoDate
      );
      expect(note).not.toBeNull();
      expect(note?.type).toBe('daily');
    });
  });

  describe('Daily note content', () => {
    it('starts with empty bullet list (no H1)', async () => {
      const note = await app.evaluate(() => window.scribe.daily.getOrCreate());

      // Verify content structure
      const children = note.content.root.children;
      expect(children).toHaveLength(1);
      expect(children[0].type).toBe('list');

      // Verify no H1
      const hasH1 = children.some(
        (c: any) => c.type === 'heading' && c.tag === 'h1'
      );
      expect(hasH1).toBe(false);
    });
  });
});
